property.item:
directive: {
	name: "trim"
}
directive: {
	name: "where"
	prefix: "WHERE "
	deprefixes: [
		"and"
		"or"
	]
}
directive: {
	name: "set"
	prefix: "SET "
	desuffixes: ","
}

transform.template:
Using Builtin Token Template Engine
-------------------------------------
input-1: @{input-1}
input-2: @{input-2}

Using Freemarker Template Engine
-------------------------------------
input_1: ${input_1}
input_2: ${input_2}

Using Pebble Template Engine
-------------------------------------
input_1: {{ input_1 }}
input_2: {{ input_2 }}
